from distutils.core import setup  from distutils.extension import Extension  from Cython.Distutils import build_ext  import numpyext_modules = [Extension("lib_amerl_c", ["lib_amerl_c.pyx"])]  setup(    name = 'aux app',    cmdclass = {'build_ext': build_ext},    ext_modules = ext_modules,  include_dirs=[numpy.get_include()])ext_modules = [Extension("lib_amerl_slope_c", ["lib_amerl_slope_c.pyx"])]  setup(    name = 'aux app',    cmdclass = {'build_ext': build_ext},    ext_modules = ext_modules,  include_dirs=[numpy.get_include()])